import styled from "styled-components";
import { Link } from "react-router-dom";
import loginLogo from "../../src/assert/login_image.jpg";
import imageRight from "../../src/assert/right_image.jpg";

const Container = styled.div`
  display: flex;
  flex-direction: row;
`;

const BlockLeft = styled.div`
  height: 59.5rem;
  background-color: #f6f6f6;
  display: flex;
  align-items: center;
  justify-content: center;
  flex: 4;
`;

const Wrapper = styled.div`
  margin-top: -10rem;
`;

const Image = styled.img`
  width: 20rem;
  height: 20rem;
  background-color: red;
`;

const Form = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  margin: 10px 10px 8px 7px;
  text-align: left;
`;

const Input = styled.input`
  border: none;
  border-radius: 4%;
  padding: 12px;
`;

const MainButton = styled.div`
  margin-bottom: -20px;
  display: flex;
  /* align-items: center; */
  /* justify-content: ; */
  justify-content: space-between;
  flex-direction: row;
  & :hover {
    cursor: pointer;
  }
`;

const Button = styled.button`
  border: none;
  margin-top: 25px;
  font-size: 20px;
  width: 10rem;
  height: 2rem;
  color: white;
  border-radius: 10%;
`;

const BlockRight = styled.div`
  background-color: white;
  flex: 6;
  height: 60rem;
`;

const ImageRight = styled.img`
  width: 100%;
  height: 90%;
  background-size: cover;
`;

export const GivePassAgain = () => {
  return (
    <div>
      <Container>
        <BlockLeft>
          <Wrapper>
            <Image src={loginLogo} />
            <Form>
              <Label
                style={{
                  fontWeight: "500",
                  textAlign: "center",
                  fontSize: "1.5rem",
                }}
              >
                Đặt lại mật khẩu
              </Label>
              <Label>Vui lòng nhập email để đặt lại mật khẩu của bạn *</Label>
              <Input placeholder="Vui lòng nhập lại Email!" type={"text"} />
              <MainButton>
                <Link to={"/"}>
                  <Button
                    style={{
                      color: "#dc7106",
                      border: "2px solid #ff9641",
                      background: "#f6f6f6",
                    }}
                  >
                    Hủy
                  </Button>
                </Link>
                <Link to={"/dat-lai-mat-khau"}>
                  <Button
                    style={{ color: "#fff9f4", backgroundColor: "#ff9138" }}
                  >
                    Tiếp tục
                  </Button>
                </Link>
              </MainButton>
            </Form>
          </Wrapper>
        </BlockLeft>
        <BlockRight>
          <ImageRight src={imageRight} />
        </BlockRight>
      </Container>
    </div>
  );
};
